import React, { Component } from 'react';
import Popup from './popup';
import '../App.css';


class Portfolioitems extends React.Component {
  state = {
    on: false,
    popupid: ""   
  }

  handlepopup(id,e)
    {
      e.preventDefault();
      this.props.handlekaropop(id);
      this.setState({
          on: !this.state.on, 
          popupid: id
        })

    }
