import React, { Component } from 'react';
import Popup from './popup';
import '../App.css';


class Portfolioitems extends React.Component {
  state = {
    on: false,
  }
  handlepopup(id)
    {

        this.props.handlekaropop(id);
        this.setState({
          on: !this.state.on,     
        })

    }

close = ( ) => {  
  this.setState({
    on:!this.state.on
  })
}
