//   {
//    let variabletosaveprojectarray = this.props.projectsinstatearray.map(projectarray => {

// if(projectarray.id===id){
//   return(projectarray)

// }

  // })}

  render() {



        return (

        <div>
          {
            this.state.on && (
              <div id="modalpage"> 

              <div  id="popupmodulediv">
              </div>

              <div className="row" id="popupdescriptionwrapper">
              <div className="col-8">

              <img className="rounded" id="projectimage" src={this.props.clickedproject.previewimage}></img>


              <button class="button button3" onClick={this.close}>X</button>

              {this.props.clickedproject.title}
              {this.props.clickedproject.categoery}
              {this.props.clickedproject.established}
              {this.props.clickedproject.details}


            </div>

             <div className="col-2"></div>
             </div>
             </div>
            )
          }
          <div  className="col-lg-4 col-md-4 col-sm-12 col-xs-12">
          <div id="cardpicture" onClick ={this.handlepopup.bind(this,this.props.project.id)}>

        <img  id="image"src ={this.props.project.previewimage}></img>
        <div id="detailsbox">
        <p>Title:   {this.props.project.title}</p>
        <p>Categoery:   {this.props.project.categoery}</p>
        <p>Created on:   {this.props.project.established}</p>
        </div>
      {}

        </div>
           </div>
           </div>
    );
  }
}
export default Portfolioitems
