import React, { Component } from 'react';
import Popup from './popup';
import '../App.css';


class Portfolioitems extends React.Component {
  state = {
    on: false,
    popupid: ""   
  }

  handlepopup(id,e)
    {
      e.preventDefault();
        this.props.handlekaropop(id);
        this.setState({
          on: !this.state.on, 
          popupid: id
        })

    }
  //   handleLangChange = () => {
  //     var lang = this.dropdown.value;
  //     this.props.onSelectLanguage(lang);            
  // }
    getNextItem = (e) => 
    {     
  e.preventDefault();



  var clicked_ID = passedid;


   var projectsinstate =this.state.projects
   projectsinstate.map(projectarray=> {
     if(projectarray.id===clicked_ID)
     {
       let index = projectsinstate.findIndex(x => x.id === clicked_ID);
       var nextindex = index + 1 ;

       this.setState({
        nextid: projectsinstate[nextindex].id
      })

// now i have to store the clicked id in a state so i can use it;


       console.log(nextindex);


       this.setState({
         modal:{showmodal:"true"},
         popupstate:
             {
                 id: projectsinstate[nextindex].id, 
                 title: projectsinstate[nextindex].title,
                 categoery:projectsinstate[nextindex].categoery,
                 established: projectsinstate[nextindex].established,
                 details: projectsinstate[nextindex].details,
                 previewimage: projectsinstate[nextindex].previewimage

             },

            });


     }  


   }



   )



}


close = ( ) => {  
  this.setState({
    on:!this.state.on
  })
}

//    handlepopup(id)
//   {
//    let variabletosaveprojectarray = this.props.projectsinstatearray.map(projectarray => {

// if(projectarray.id===id){
//   return(projectarray)

// }

  // })}

  render() {



        return (

        <div>
          {
            this.state.on && (
              <div id="modalpage"> 

              <div  id="popupmodulediv">
              </div>

              <div  id="popupdescriptionwrapper">
              <div id="wrapper">
              <div id="popupbar"></div>
              </div>
              <div id="descriptionwrapper">
              <img id="projectimage" src={this.props.clickedproject.previewimage}></img>


              <button id="cancelbutton" class="button button3" onClick={this.close}>Close</button>
            <div id="descriptionwrapperfordetails">
              <p>Title:  &nbsp; {this.props.clickedproject.title}</p><pre></pre>
              <p>Categoery:  &nbsp;{this.props.clickedproject.categoery}</p><pre></pre>
             <p>Created: &nbsp; {this.props.clickedproject.established}</p><pre></pre>
             <p>Description of project: &nbsp;{this.props.clickedproject.details}</p>
             <button id="nextbutton" class="button button3" onClick ={this.getNextItem.bind(this)}>next</button>
              </div>


              </div>
              </div>
            </div>  

            )
          }
          <div  className="col-lg-4 col-md-4 col-sm-12 col-xs-12">
          <div id="cardpicture" onClick ={this.handlepopup.bind(this,this.props.project.id)}>

        <img  id="image"src ={this.props.project.previewimage}></img>
        <div id="detailsbox">

        <div className="row"><p>title:   {this.props.project.title}</p></div>
        <div className="row"><p>Categoery:   {this.props.project.categoery}</p></div>
        <div className="row"><p>Created on:   {this.props.project.established}</p></div>
        </div>


        </div>
           </div>
           </div>
    );
  }
}
export default Portfolioitems
